angular.module('playIosAngular', ['wakConnectorModule']);function PlayIosController($scope, wakConnectorService) {    // Create a proxy of the server model	wakConnectorService.init('Country').then(function () {	    PlayIosControllerReady($scope, wakConnectorService);        });}function PlayIosControllerReady($scope, $wakanda) {		var Country = $wakanda.getDatastore().Country;    Country.$find({}).then(function(event) {        $scope.countries = event.result;    });}